import {useState, useContext} from 'react'
import FormInput from '../form-input/form-input.component'
import { UserContext } from '../../context/user.context'
import Button from '../button/button.component'
import './sign-in-form.styles.scss'
import { signInWithGooglePopup, createUserDocumentFromAuth, signInUserWithEmailAndPassword } from '../../utils/firebase/firebase.utils'

const defaultFormFields = {
    email: '',
    password: '',
}

const SignInform = () => {

    const [formFields, setFormFields] = useState(defaultFormFields)
    const {email, password} = formFields

    const {setCurrentUser} = useContext(UserContext)

    const resetFormFields = () => {
        setFormFields(defaultFormFields)
    }

    const handleSubmit = async (event) => {
        event.preventDefault()

        try {
            const {user} = await signInUserWithEmailAndPassword (email, password)
            console.log(user)
            setCurrentUser(user)
            resetFormFields()
        } catch(err) {
            switch(err.code) {
                case "auth/wrong-password":
                    alert("incorrect password")
                    break
                case "auth/user-not-found":
                    alert("no user associated with this email")
                    break
                default:
                    console.log(err)
            }
        }
    }

    const handleChange = (event) => {
        const {name, value} = event.target

        setFormFields({...formFields, [name]:value})
    }

    const signInWithGoogle = async () => {
        const {user} = await signInWithGooglePopup()
        await createUserDocumentFromAuth(user)
        setCurrentUser(user)
    }

    return(
        <div className='sign-up-container'>
            <h2>Already have an account?</h2>
            <span>Sign in with your email and password</span>
            <form onSubmit={handleSubmit}>
                <FormInput label="Email"  type="email" required name="email" onChange={handleChange} value={email}/>

                <FormInput label="Password"  type="password" required name="password" onChange={handleChange} value={password}/>
                <div className='buttons-container'>
                    <Button type="submit">Sign In</Button>
                    <Button buttonType='google' onClick={signInWithGoogle} type="submit">Google Sign In</Button>
                </div>        
            </form>
        </div>
    )
}

export default SignInform
