import { takeLatest, put, all, call } from "redux-saga/effects";
import { USER_ACTION_TYPES } from "./user.types";
import { signInFailed, signInSuccess } from "./user.action";

import { getCurrentUser, createUserDocumentFromAuth, signInWithGooglePopup, signInUserWithEmailAndPassword, createAuthUserWithEmailAndPassword } from "../../utils/firebase/firebase.utils";

export function* getSnapShotFromUserAuth(userAuth, additionalDetails) {
    console.log("5")
    try {
        const userSnapshot = yield call(createUserDocumentFromAuth, userAuth, additionalDetails)
        console.log(userSnapshot.data())
        yield put(signInSuccess({id: userSnapshot.id, ...userSnapshot.data()}))
    } catch(err) {
        yield put(signInFailed(err))
    }
}

export function* signInWithGoogle() {
    try {
        const {user} = yield call(signInWithGooglePopup)
        yield call(getSnapShotFromUserAuth, user)
    } catch(err){
        yield put(signInFailed(err))
    }
}

export function* signInWithEmail({payload: {email, password}}){
    console.log(email, password)
    console.log("4")
    try {
        const {user} = yield call(signInUserWithEmailAndPassword, email, password)
        yield call(getSnapShotFromUserAuth, user)
    } catch(err) {
        yield put(signInFailed(err))
    }
}

export function* signUpWithEmail({payload: {email, password}}){
    console.log(email, password)
    console.log("4")
    try {
        const {user} = yield call(createAuthUserWithEmailAndPassword, email, password)
        yield call(getSnapShotFromUserAuth, user)
    } catch(err) {
        yield put(signInFailed(err))
    }
}

export function* isUserAuthenticated(){
    try {
        const userAuth = yield call(getCurrentUser)
        if(!userAuth) return
        yield call(getSnapShotFromUserAuth, userAuth)
    } catch(err){
        yield put(signInFailed(err))
    }
}

export function* onGoogleSignInStart() {
    yield takeLatest(USER_ACTION_TYPES.GOOGLE_SIGN_IN_START, signInWithGoogle)
}

export function* onCheckUserSession() {
    yield takeLatest(USER_ACTION_TYPES.CHECK_USER_SESSION, isUserAuthenticated)
}

export function* onEmailSignInStart() {
    yield takeLatest(USER_ACTION_TYPES.EMAIL_SIGN_IN_START, signInWithEmail)
}

export function* onEmailSignUpStart() {
    yield takeLatest(USER_ACTION_TYPES.EMAIL_SIGN_UP_START, signUpWithEmail)
}

export function* userSagas() {
    yield all([call(onCheckUserSession), call(onGoogleSignInStart), call(onEmailSignInStart)])
}
